mapper.select:
SELECT
FROM tb_word w
AND w.word = #{word, jdbcType=VARCHAR}
AND w.word_id IN
#{wordIdItem, jdbcType=BIGINT}
ORDER BY word_id

SELECT 
	w.word_id, w.word, e.exhibit_name, w.total_count, count(wo.count) as operateCount, w.in_date, w.out_date
FROM tb_word w LEFT JOIN tb_word_operate wo ON w.word_id = wo.word_id 
	LEFT JOIN tb_exhibit e ON w.exhibit_id = e.exhibit_id


		AND w.word = #{word, jdbcType=VARCHAR}


		AND w.word IN

			#{wordItem, jdbcType=VARCHAR}



		AND w.exhibit_id IN

			#{exhibitIdItem, jdbcType=BIGINT}



		AND wo.type = #{operateType, jdbcType=INTEGER}


		AND wo.operate_date  >=  #{updateDateMin}


		AND wo.operate_date  <=  #{updateDateMax}


		AND w.total_count  >=  #{totalCountMin}


		AND w.total_count  <=  #{totalCountMax}


	GROUP BY wo.word_id

mapper.update:
UPDATE tb_word
SET

	in_date = #{inDate,jdbcType=TIMESTAMP},


	out_date = #{outDate,jdbcType=TIMESTAMP},

total_count = #{totalCount,jdbcType=BIGINT}
WHERE word_id = #{wordId,jdbcType=BIGINT}
