mapper.select:
SELECT

FROM 
	tb_word w 


		AND w.word = #{word, jdbcType=VARCHAR}


		AND w.word_id IN 

			#{wordIdItem, jdbcType=BIGINT}



		AND w.exhibit_id = #{exhibitId, jdbcType=BIGINT}


ORDER BY word_id

SELECT w.word_id, w.word, wi.exhibit_name, s.boothIdStr, s.boothNameStr, w.total_count, count(wi.in_number) as inTotalCount, w.in_date FROM tb_word w LEFT JOIN tb_word_in wi ON w.word_id = wi.word_id LEFT JOIN ( SELECT group_concat(s2.booth_id) boothIdStr, group_concat(s2.booth_name) boothNameStr, s1.word FROM tb_word s1 INNER JOIN tb_booth_word s2 ON s1.word = s2.word WHERE s2.exhibit_id IN
#{exhibitIdItem, jdbcType=BIGINT}
GROUP BY s1.word ) s ON w.word = s.word WHERE w.exhibit_id IN
#{exhibitIdItem, jdbcType=BIGINT}
AND w.word = #{word, jdbcType=VARCHAR}
AND w.word IN
#{wordItem, jdbcType=VARCHAR}
AND w.total_count >= #{totalCountMin}
AND w.total_count <= #{totalCountMax}
AND wi.in_date >= #{updateDateMin}
AND wi.in_date <= #{updateDateMax}
GROUP BY wi.word_id

mapper.update:
UPDATE tb_word
SET

	in_date = #{inDate,jdbcType=TIMESTAMP},


	out_date = #{outDate,jdbcType=TIMESTAMP},

total_count = #{totalCount,jdbcType=BIGINT}
WHERE word_id = #{wordId,jdbcType=BIGINT}
